// If submission has origin id, and origin id submission has not yet been loaded
if (submission('originId') && $('[data-loaded-id=' + submission('originId') + ']').length === 0) {
  var loadingContent = $('<div>', { class: 'paper-form' }).append(
    $('<div>', { class: 'section-title text-muted' }).append(
      $('<span>', { class: 'fa fa-spinner fa-spin mr-2' }),
      K.translate('Loading')
    )
  );

  // Show before review container
  K('content[Before Review Container]').show();
  // Create container to render into
  const container = $('<div>', { class: 'mb-3',  'data-loaded-id': submission('originId') })
    .append(loadingContent.clone())
    .appendTo($(K('content[Before Review Container]').element()));
  // Load the original submission in review
  K.load({
    parentForm: K('form'),
    path: '/submissions/' + submission('originId') + '?review',
    container: container.get(0)
  });

  // Fetch descendants of the original submission, which are siblings of this one, to render in review
  $.ajax({
    url: "/app/api/v1/submissions/"+submission('originId')+"?include=descendants", 
    success: function(response){
      // Get the siblings that are not in draft and are not this submission
      const siblings = response.submission.descendants.filter(function(s) {
        return s.id !== submission('id') && s.coreState !== 'Draft';
      });
      // Get the siblings that came before this submission
      const previousSiblings = siblings.filter(function(s) {
          return s.submittedAt < submission('createdAt');
        });
      // Get the siblings that came after this submission
      const nextSiblings = siblings.filter(function(s) {
          return !!submission('submittedAt') && s.submittedAt > submission('submittedAt');
        });

      // Show previous siblings if any exist
      if (previousSiblings && previousSiblings.length > 0) {
        // Show before review container
        K('content[Before Review Container]').show();
        // For each previous sibling, render it in review
        previousSiblings.forEach(function(sibling) {
          // Create container to render it into
          const container = $('<div>', { class: 'mb-3',  'data-loaded-id': sibling.id })
            .append(loadingContent.clone())
            .appendTo($(K('content[Before Review Container]').element()));
          // Load the sibling submission in review
          K.load({
            parentForm: K('form'),
            path: '/submissions/' + sibling.id + '?review',
            container: container.get(0)
          });
        });
      }

      // Show next siblings if any exist
      if (nextSiblings && nextSiblings.length > 0) {
        // Show after review container
        K('content[After Review Container]').show();
        // For each next  sibling, render it in review
        nextSiblings.forEach(function(sibling) {
          // Create container to render it into
          const container = $('<div>', { class: 'mt-3', 'data-loaded-id': sibling.id })
            .append(loadingContent.clone())
            .appendTo($(K('content[After Review Container]').element()));
          // Load the sibling submission in review
          K.load({
            parentForm: K('form'),
            path: '/submissions/' + sibling.id + '?review',
            container: container.get(0)
          });
        });
      }
    }
  });
}

var ModalHeaderContentRenderer = function(props, options) {
  return [
    {
      tag: 'h4',
      props: { className: 'modal-title' },
      children: [
        {
          tag: 'button',
          props: { type: 'button', className: 'btn btn-link', onClick: props.toggle },
          children: K.translate('Close'),
        },
        { tag: 'span', children: K.translate(props.title) },
        !props.signed &&
          props.valid && {
            tag: 'button',
            props: {
              type: 'button',
              className: 'btn btn-link',
              onClick: props.reset,
            },
            children: K.translate('Reset'),
          },
      ].filter(Boolean),
    },
    {
      tag: 'small',
      props: { className: 'text-muted px-3 py-1' },
      children: K.translate(
        "Signature of the IAO or Appointee of the office responsible for approving access to the system being requested."
      ),
    },
  ];
};

