// Add help text to labels
$(K('form').element()).find(
  '[data-help-text][data-element-type=section].inline-group,[data-help-text][data-element-type=wrapper]'
).each((i, el) => {
  $(el)
    .children('.inline-group-label,.field-label')
    .filter((idx, label) => {
      return $(label).has('em.help-text').length === 0;
    })
    .append(
      $('<em>', { class: 'help-text font-sm' }).text(
        $(el).attr('data-help-text'),
      ),
    );
});
