/* This original request will render its decendants in review as long as it hasn't been loaded by one of those descendants. This is tracked by adding a data-loaded-id attribute to the container of each submission that is loaded as a review subform. If that attribute with the current submission's id doesn't exist in the dom, then it is safe to render the descendants in review. Otherwise, each descendant will render it's appropriate siblings so we do nothing here. */
if ($('[data-loaded-id=' + submission('id') + ']').length === 0) {
  // Add data-loaded-id attribute to current form so descendant subforms don't try to load it again
  $(K('form').element()).attr('data-loaded-id', submission('id'));
  // Fetch descendants of the submission
  $.ajax({
    url: "/app/api/v1/submissions/"+submission('id')+"?include=descendants", 
    success: function(response){
      // Get the descendants that are not in draft
      const descendants = response.submission.descendants.filter(function(s) {
        return s.coreState !== 'Draft';
      });

      // Show descendants if any exist
      if (descendants && descendants.length > 0) {
        // Show before review container
        K('content[Review Container]').show();
        // For each descendants, render it in review
        descendants.forEach(function(descendant) {
          // Create container to render it into
          const container = $('<div>', { class: 'mt-3 form-unstyled', 'data-loaded-id': descendant.id })
            .append(
              $('<div>', { class: 'paper-form' }).append(
                $('<div>', { class: 'section-title text-muted' }).append(
                  $('<span>', { class: 'fa fa-spinner fa-spin mr-2' }),
                  K.translate('Loading')
                )
              )
            )
            .appendTo($(K('content[Review Container]').element()));
          // Load the sibling submission in review
          K.load({
            parentForm: K('form'),
            path: '/submissions/' + descendant.id + '?review',
            container: container.get(0)
          });
        });
      }
    }
  });
}

// Add help text to labels
$(K('form').element()).find(
  '[data-help-text][data-element-type=section].inline-group,[data-help-text][data-element-type=wrapper]'
).each((i, el) => {
  $(el)
    .children('.inline-group-label,.field-label')
    .filter((idx, label) => {
      return $(label).has('em.help-text').length === 0;
    })
    .append(
      $('<em>', { class: 'help-text font-sm' }).text(
        $(el).attr('data-help-text'),
      ),
    );
});
